-- ----------------------------
-- Table structure for articles
-- ----------------------------
DROP TABLE IF EXISTS `articles`;
CREATE TABLE `articles` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章标题',
  `category` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章分类id',
  `guest_book_id` int(11) DEFAULT NULL COMMENT '留言id',
  `description` text COLLATE utf8mb4_unicode_ci COMMENT '文章简介',
  `photo` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '文章封面',
  `keywords` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '文章关键字',
  `click` int(11) NOT NULL DEFAULT '0' COMMENT '文章点击量',
  `publish_time` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章发布时间',
  `author` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '作者,程序默认存储登录用户',
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章内容',
  `discuss` tinyint(1) DEFAULT '1',
  `recommend` tinyint(1) DEFAULT '0',
  `top` tinyint(1) DEFAULT NULL,
  `template_id` int(11) DEFAULT NULL,
  `deleted_at` date DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ----------------------------
-- Records of articles
-- ----------------------------
INSERT INTO `articles` VALUES ('40', '建筑证书管理：建筑工程施工许可事项操作规程（天津）', '行业动态', null, '各省市在建筑工程施工许可事项所需提供的材料上或多或少有一些差异，今天哼哼办公小编给大家介绍的是天津市建筑工程施工许可事项操作规程；包含申请材料、申请条件及审查标准，供大家参考！', 'http://www.hhbg.com/storage/ww4GjCRYFVML4uSxdrtRujwOf9vbTTFiKAscOzL8.png', '哼哼办公', '22', '2018-12-12', '1', '<p><br/></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">各省市在建筑工程施工许可事项所需提供的材料上或多或少有一些差异，今天哼哼办公小编给大家介绍的是天津市建筑工程施工许可事项操作规程；包含申请材料、申请条件及审查标准，供大家参考！</span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体;\">申请材料</span></strong></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">1、<span style=\"font-size: 16px; font-family: 宋体;\">申办行政许可项目承诺书</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">2、<span style=\"font-size: 16px; font-family: 宋体;\">委托书</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">3、<span style=\"font-size: 16px; font-family: 宋体;\">建筑工程施工许可申请表</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">4、<span style=\"font-size: 16px; font-family: 宋体;\">用地批准手续（提供原件及复印件，用地批准手续包括房地产权证、用地批准书和土地使用证书等</span>,<span style=\"font-size: 16px; font-family: 宋体;\">具备以上任一证件即可，证件内容与申报内容相符。）</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 14px;\">5、<span style=\"font-size: 16px; font-family: 宋体;\">天津市城市基础设施配套基金申请表（涉及缴费的建设内容与申请建筑工程施工许可证的建设内容一致，此材料可信用承诺。</span></span> <span style=\"font-size: 16px; font-family: 宋体;\">）</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">6、<span style=\"font-size: 16px; font-family: 宋体;\">施工图设计文件审查合格证（提供原件及复印件，施工设计审查合格书中审查的建设内容与建设工程规划许可证相符，合格书中建设单位、勘察单位、设计单位与申报项目信息一致。）</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">7、<span style=\"font-size: 16px; font-family: 宋体;\">依法确定承包的材料（提供原件及复印件，依法确定承包的材料包括施工中标通知书、直接发包登记表等及施工承包合同，提供材料建设内容与申报内容相符。）</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">8、<span style=\"font-size: 16px; font-family: 宋体;\">天津市房屋建筑或市政基础设施工程质量安全登记（已办理的工程质量监督登记通知及安全施工措施备案通知书内建设规模与施工中标规模、施工图审计文件审查合格书规模一致。）</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">9、<span style=\"font-size: 16px; font-family: 宋体;\">施工企业主要技术负责人签署的已经具备施工条件意见书</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">10、<span style=\"font-size: 16px; font-family: 宋体;\">建设工程规划许可证（提供原件及复印件，建设工程规划许可证需附建设工程规划许可通知书，证件内建设单位与申报该项目的建设单位一致，通知书内单位工程信息与申报建设内容相符，使用建设工程规划许可部位证申请建筑工程施工许可部位证的，通知书内注明的有效期未逾期。）</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;<img src=\"http://www.blog.com/ueditor/php/upload/image/20181212/1544594581.png\" title=\"1544594581.png\" alt=\"4n.png\"/></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体;\">申请条件</span></strong></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">a)依法应当办理用地批准手续的，已经办理该建筑工程用地批准手续；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">b)在城市、镇规划区的建筑工程，已经取得建设工程规划许可证；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">c)施工场地已经基本具备施工条件，需要征收房屋的，其进度符合施工要求；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">d)已经确定施工企业；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">e)有满足施工需要的技术资料，施工图设计文件已按规定审查合格；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">f)有保证工程质量和安全的具体措施；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">g)按照规定应当委托监理的工程已委托监理；</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">h)建设资金已经落实。</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体;\">审查标准</span></strong></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></strong></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">1.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">建筑工程施工许可申请表应主要审查以下内容：</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">a)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">建筑工程施工许可证申请表填报内容与网上申报所填报内容相符；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">b)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">加盖建设单位公章；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">c)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">有法定代表人签章、签字。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">2.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">用地批准手续应主要审查以下内容：</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">a)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">用地批准手续包括房地产权证、用地审批书、土地招拍挂协议书、土地划拨决定书、土地租赁协议和土地使用证书、土地作价入股协议和土地使用证书等；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">b)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">核实原件确保在有效期内，留存复印件。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">3.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">建设工程规划许可证应主要审查以下内容：</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">a)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">建设工程规划许可证，需附建设工程规划许可通知书；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">b)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">核实原件，规划证内项目信息与申报内容是否相符；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">c)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">使用建设工程规划许可部位证申请建筑工程施工许可部位证的，通知书内注明的有效期未逾期；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">d)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">留存复印件。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">4.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">施工图设计文件审查合格书应主要审查以下内容：</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">a)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">施工设计审查合格书中审查的建设内容与建设工程规划许可证相符；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">b)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">合格书中建设单位、勘察单位、设计单位与申报项目信息一致；</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">c)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">核实原件，留存复印件。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">5.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">施工中标通知书及备案的施工承包合同和工程监理合同应主要审查：审查施工招标通知书、施工承包合同和工程监理合同内容与申报内容是否一致，施工承包合同和工程监理合同是否备案。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">6.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">工程质量监督登记通知书和安全措施备案通知书应主要审查以下内容：审查建设单位信息、施工单位信息、建设规模等内容与申报内容是否一致。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">7.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">施工企业主要技术负责人签署的已具备施工条件意见书应主要审查以下内容：审查是否有施工单位现场负责人的签字、签章。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">8.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">建筑节能技术资料应主要审查以下内容：审查文件内容与申报内容是否一致。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">9.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">截止申请之日无拖欠工程情形的承诺书或者能够表明其无拖欠工程款情形的其他材料应主要审查以下内容：审查文件承诺日期与申请施工许可证的日期是否一致，是否有法定代表人的签字、签章及建设单位公章。</span></span></p><p style=\"margin-left: 0px; text-indent: 0px; text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 14px;\">10.&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">银行出具的到位资金证明或银行付款保函或年度投资计划批注那文件应主要审查以下内容：</span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 14px;\">a)&nbsp;<span style=\"font-size: 16px; font-family: 宋体;\">审查非政府投资项目建设单位开户银行出具的资金是否到位（建设工期不足一年的，到位资金不少于工程合同价的</span>50%<span style=\"font-size: 16px; font-family: 宋体;\">；建设工程超过一年的，到位资金不少于工程合同价的</span><span style=\"font-size: 16px; font-family: Calibri;\">30%</span><span style=\"font-size: 16px; font-family: 宋体;\">）；</span><span style=\"font-size: 16px; font-family: Calibri;\">b)</span></span> <span style=\"font-family: 宋体; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 宋体;\">审查政府投资项目是否提供该项目当年的年度投资计划文件，核对原件，留存复印件。</span>11.<span style=\"font-size: 16px; font-family: 宋体;\">新建住宅和公建大配套缴费证明应主要审查以下内容：审查涉及缴费的建设内容与申请建设内容是否一致。</span></span></span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify; line-height: 1.75em; margin-top: 10px;\"><a href=\"http://www.58jzr.com\" target=\"_self\"><span style=\"font-family: 宋体; font-size: 16px;\">建筑人管理系统</span></a><span style=\"font-family: 宋体; font-size: 16px;\">助力建筑企业资质管理、证书管理、项目管理；到期预警更贴心！智能匹配更省心！</span></p><p><br/></p>', '1', '1', null, '1', null);
INSERT INTO `articles` VALUES ('41', '证书预警很重要，但这一点也不可忽视', '行业动态', null, '吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商', 'http://www.hhbg.com/storage/EP15cIpkblfGYjuJqCHy6r6eNRzyxVclX4zyl5Tf.jpeg', null, '6', '2018-12-12', '1', '<p style=\"line-height: 2em; margin-top: 10px;\"><br/></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 微软雅黑;\">建筑企业证书类型多，数量大，不同证书管理方式有所变化。建筑企业人员证书管理的工作，从外行人的角度看，可能只是认为只是证书管理，做个</span>excel表格就搞定了<span style=\"font-size: 16px; font-family: 微软雅黑;\">，看起来感觉很简单。但其实其实很繁琐，不仅要统计证书数量，还要将证书分类要，时常关注行业关于人员证书的新闻也是日常重点工作。<img src=\"http://www.hhbg.com/ueditor/php/upload/image/20181212/1544598864.jpg\" title=\"1544598864.jpg\" alt=\"3re.jpg\"/></span></span></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">大部分建筑企业为企业资质和管理的需要，设置专人管理，并且每年投入大量成本在人员证书配置上。但是人工管理难免会有一些遗漏，企业因为人员证书管理问题导致非标，造成企业的直接损失。如果企业证书管理水平和效率的提升，减少证书浪费，能为企业节省不少的费用。企业制定科学的证书管理有利于提高证书管理效率，将证书管理细致到每一个环节、每一本证书的管理。现在就教你如何轻松管理各种证书</span></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">一、<span style=\"font-size: 16px; font-family: 微软雅黑;\">证书分类归档，对比数据统计</span></span></strong></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.&nbsp;<span style=\"font-size: 16px; font-family: 微软雅黑;\">统计分类：建筑企业需要将当前所持有的证书进行盘点，第一层分类，分为企业相关证书和人员相关证书。将企业证书作为一个大类存放，人员相关证书进行第二层分类，按照证书类型，注册类、职业技能类等进行详细的分类。并分别列出各自所对应的证书数量和企业所需各类证书数量之间的差距，提前做好所需证书来源规划。</span></span></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.&nbsp;<span style=\"font-size: 16px; font-family: 微软雅黑;\">建立台账：将证书分类，再细化人员详细信息，包括证书名称、证书持有者、证书有效期等完整信息。</span></span></p><p style=\"text-indent: 28px; text-align: justify; line-height: 2em; margin-top: 10px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 16px;\">二、借还流程完整</span></strong></p><p style=\"text-align: justify; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">企业人员证书借用，随意性较大，证书流转时间太长，责任人不清，容易出现证书丢失或者不知道证书使用的情况。建立证书借还审批管理制度，每一本证书的借出、转让、归还记录清楚，责任明确到个人，借还流程更规范，证书管理更有效率。</span></p><p style=\"text-align:center\"><span style=\"font-family: 微软雅黑; font-size: 16px;\"><img src=\"http://www.hhbg.com/ueditor/php/upload/image/20181212/1544598957.jpg\" title=\"1544598957.jpg\" alt=\"4c.jpg\"/></span></p><p style=\"line-height: 2em; margin-top: 10px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 16px;\">三、证书时间预警</span></strong></p><p style=\"text-indent: 28px; text-align: justify; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">证书过期会影响企业投标和资质业务的办理，证书管理部门需要时刻关注证书期限，根据证书时间期限管理表单，根据时间的推进定期或者不定期进行时间期限的整理，避免忘记或者遗漏造成更大的损失。</span></p><p style=\"margin-left: 0px; text-indent: 28px; line-height: 2em; margin-top: 10px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">二、<span style=\"font-size: 16px; font-family: 微软雅黑;\">证书延续办理</span></span></strong></p><p style=\"text-indent: 28px; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">证书延续办理手续的重要性不可忽视，过期证书可能会造成企业项目损失。证书预警关注证书到期时间，在证书即将到期之前做好证书延续手续的准备。根据即将到期书类型办理不同的延续手续。组织需要继续教育的人员开展接受继续教育的工作，统计证书延续需要考试的人员息以及无证书人员，证书考试详细信息。包括考试时间、考试类型、考试结果等信息，及时掌握人员考试状态，方面开展下一步的工作安排。</span></p><p style=\"line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p><br/></p>', '1', '0', null, '1', null);
INSERT INTO `articles` VALUES ('43', '建企触及这13种行为，将被拉入黑名单，你中招了吗？', '行业动态', null, '吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商务局)局长周希勇1.11月30日，吉林省通化市工道江区经济技术合作局(商', 'http://www.hhbg.com/storage/1XttTwquQG6wv7yMdb4uRT6vftM8DtKmXYjHakhJ.png', null, '3', '2018-12-12', '1', '<p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; line-height: 28px; font-size: 14px;\">近年来，国家颁布各种条例和采取一系列措施整治企业运营不规范的行为，建立企业信用黑名单制度。企业信用黑名单关系到企业的运营发展，一旦企业被列入信用黑名单，很可能在后续的经营发展中处处受限。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; line-height: 28px; font-size: 14px;\">建筑人证书管理系统小编总结了以下几种作为建筑企业比较容易被列入黑名单的情形，各企业可以根据对应的情况及时整改企业不规范行为，避免企业因为信用问题带来更大的损失。</span></p><p style=\"margin-top: 10px; white-space: normal; text-align: center; line-height: 2em;\"><span style=\"font-family: 微软雅黑; line-height: 28px; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20181212/1544599687.jpg\" title=\"建筑人管理系统：建企触及这13种行为，将被拉入黑名单，你中招了吗？\" alt=\"建筑人管理系统：建企触及这13种行为，将被拉入黑名单，你中招了吗？\" width=\"800\" height=\"533\" style=\"width: 800px; height: 533px;\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\">一、</span><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">农民工工资拖欠</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">建筑企业存在克扣或者无故拖欠农民工工资达到一定数额的行为。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">因为工资拖欠引起较大群体事件、恶性事件、极端事件发生并影响社会公共秩序的情况。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">3.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">企业欠薪，引起群体讨薪，被媒体曝光的情况。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2017年9月人力资源社会保障部关于印发《拖欠农民工工资“黑名单”管理暂行办法》自2018年1月1日起施行。</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">用人单位存在《</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">办法</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">》中所列情形之一的，人力资源社会保障行政部门应当自查处违法行为并作出行政处理或处罚决定之日起20个工作日内，按照管辖权限将其列入拖欠工资“黑名单”。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\">二、</span><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">社保缴纳不规范</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">社保缴纳标准未足额：企业为员工缴纳社保未</span><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">按照社保基数的规定，忽略基数上下限，无论工资高低，一律按照最低工资标准交社保。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">社保缴纳不规范：企业借用劳务派遣员工、兼职人员、试用期员工、与员工签订自愿放弃购买社保协议等借口拒绝为员工购买社保的行为。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">2018年11月29日</span><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">国家发展改革委等28部门联合印发《关于对社会保险领域严重失信企业及其有关人员实施联合惩戒的合作备忘录》表示存在存在备忘录中有关社保失信情形将被列入社保严重失信、失范行为惩戒名单。社保作为国家重点整治内容，建筑企业应尽快将应缴纳的社保补缴完成，以免被列入企业黑名单。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">三、</span><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">税款缴纳作假</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">企业账簿作假：通过少列收入、伪造账目、凭证作假等情况少缴或者拒缴应纳税金额超过10万元以上。任一年度不缴或者少缴应纳税款占当年各税种应纳税总额的10%。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">通过各种手段隐藏财产，暴力、威胁方法拒绝纳税，骗取国家进出口退税的行为。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">3.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">发票作假、重大金额偷税漏税以及其他不合法的缴税行为。</span></p><p style=\"margin-top: 10px; margin-bottom: 0px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(246, 246, 246); line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">国家税务总局近期发布《重大税收违法失信案件信息公布办法》，自2019年1月1日起施行。《办法》修改了逃避追缴欠税标准，欠缴税款列入“黑名单”的起点由100万元降至10万元。</span></p><p style=\"margin-top: 10px; white-space: normal; text-align: center; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20181212/1544599688.jpg\" title=\"建筑人管理系统：建企触及这13种行为，将被拉入黑名单，你中招了吗？\" alt=\"建筑人管理系统：建企触及这13种行为，将被拉入黑名单，你中招了吗？\" width=\"800\" height=\"502\" style=\"width: 800px; height: 502px;\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\">四、</span><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">企业资质作假、工程重大质量安全事故</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">业绩材料作假，大部分企业不重视业绩文件资料管理，等到资质申报的时候，通过各种手段造假业绩材料。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">业绩材料管理不当：企业无专人管理业绩材料，各种数据信息不齐全，时间期限模糊不清，业绩材料整理归档混乱。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">3.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">工程质量安全事故：企业企业设备管理不到位、材料采购不规范。工程施工场地出现因工程质量造成重大事故人员伤亡的情况，受到行政处罚的情况。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">4.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">工程款拖欠：建筑企业被法院判决或仲裁机构裁决，认定为拖欠工程款，且拒不履行生效法律文书确定的义务的。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">5.&nbsp;</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">2017年12月由中华人民共和国住房和城乡建设部印发《建筑市场信用管理暂行办法》在房屋建筑和市政基础设施工程建设活动中，对建筑市场各方主体信用信息的认定、采集、交换、公开、评价、使用及监督管理。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 2em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">除了以上情形之外，全国各地也采取积极的整治措施，制定各自区域相应更加细致的信用黑名单制度，详情信息可以参考各省市企业信用黑名单相关条例。</span></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; text-align: center; line-height: 2em;\"><br/></p><p><br/></p>', '1', '0', null, '1', null);
INSERT INTO `articles` VALUES ('47', '中标率低，成本高？教你一招提升中标率', '行业动态', null, '随着建筑行业竞争日趋激烈，BIM技术不再只是一个趋势，而是建筑企业转型升级的一大优势。BIM技术的应用推动工程项目各个环节、阶段的有效衔接，较大的提高了建筑工程信息集成化程度。那么建筑企业应用BIM技术的优势有哪些呢？', 'http://www.hhbg.com/storage/POtj7p8kLi2xcnAeoQ9X9uFXkJm5v0PfcJXgfhnm.jpeg', '哼哼办公,OA管理系统,通过后天', '3', '2018-12-12', '1', '<p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; text-align: justify; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">随着建筑行业竞争日趋激烈，BIM技术不再只是一个趋势，而是建筑企业转型升级的一大优势。BIM技术的应用推动工程项目各个环节、阶段的有效衔接，较大的提高了建筑工程信息集成化程度。那么建筑企业应用BIM技术的优势有哪些呢？</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20181212/1544601572.png\" title=\"image.png\" alt=\"image.png\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">一、增强企业中标竞争力，提升中标率</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.帮助企业进行快速完成投标：BIM 能够帮助投标企业更深入了解投标项目具体情况，</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">灵活、精准、</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">高效地完成工程量计算</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">。保障</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">招标清单的完整、清单工程量的准确</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">，解决招标阶段工程量清单的难点。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.投标报价是企业中标的关键，投标企业利用BIM模型与企业成本数据库科学结合应用。</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">编制准确的工程量清单，</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">制定合理的报价，帮助投标单位进行快速投标。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">3.BIM能够根据数据调整为</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">设计人员提供不同的优质设计方案，避免在数据上出错，便于设计人员优化设计方案。</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20181212/1544601573.jpg\" title=\"csf.jpg\" alt=\"csf.jpg\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">二、施工方案模拟，提升企业效益</span></strong></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">1.</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">强大的模拟功能：</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">BIM通过模拟建筑整体效果，包括模拟紧急疏散、日照、</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">节能、热能传导等，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">对建筑结构相关信息进行有效分析，评估建筑设计的合理性。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">2.提升企业管理:BIM技术能够帮助企业积累更多的资源和数据，合理配置工程资金，减少资金浪费，较大限度节约企业资金成本。有利于增强企业自身管理能力和工程项目管理能力，提升企</span><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px; background: rgb(255, 255, 255);\">业精细化管理水平</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 14px;\">3.企业利用BIM 模型，可以有效避免工程量清单中出现漏项和错算的情况，减少施工中跟工程量有关的纠纷，减少不必要的资金浪费，</span><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 14px;\">有效控制工程施工中的预算，防止企业亏损。</span></p><p style=\"margin-top: 10px; white-space: normal; text-indent: 28px; line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 14px;\">4.信息化手段支撑工程关键性工作：利用BIM实现工程项目中建筑施工各个阶段的模拟管理，根据系统数据对比现实施工作业中的差异；及时找到施工现场存在的问题，对建筑场地进行进一步的修改和设计规划，减少返工，施工变更情况，</span><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 14px;\">提升施工效率。</span></p><p style=\"margin-top: 10px; margin-bottom: 0px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-size: 14px;\">5.</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">通过BIM模型制定合理的合同清单价格，实现工程量追踪，有效控制掌握施工情况与施工进度，有效控制施工过程中的变更情况，避免费用结算超出预算，提升企业效益。</span></p><p style=\"margin-top: 10px; margin-bottom: 0px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; margin-bottom: 0px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; margin-bottom: 0px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: center;\"><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('48', '工程监理的“尴尬”处境，到底该如何化解？', '行业动态', null, '近年来，工程监理的性质相互矛盾、监理工程师的角色模糊不清、工程监理的三大控制目标难以实现、业主的行为不规范和工程监理的阶段范围不明确等。这些问题的产生导致我国监理工作的范围越来越狭窄，监理人才流失严重；工程监理的基本任务即“三控”“两管”“一协调”无法全面落实；监理工程师正扮演一个质检员或安全员的角色。', 'http://www.hhbg.com/storage/leGfgRUdi4wptz0JDS2Q5A5XheAPkDfY7llF6cAG.jpeg', '建筑咨询行业管理系统,哼哼办公,oa管理系统', '13', '2018-12-12', '1', '<p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp;&nbsp; &nbsp; &nbsp;近年来，工程监理的性质相互矛盾、监理工程师的角色模糊不清、工程监理的三大控制目标难以实现、业主的行为不规范和工程监理的阶段范围不明确等。这些问题的产生导致我国监理工作的范围越来越狭窄，监理人才流失严重；工程监理的基本任务即“三控”“两管”“一协调”无法全面落实；监理工程师正扮演一个质检员或安全员的角色。</p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;政府各部门关于监理方面的消息也不绝于耳，（见建筑经济与管理之前发布的相应文章：最新消息：监理、造价咨询资质简化或取消摆上日程；监理企业已面临“绝境”？官方最新消息来了~）因此，监理需要重新定位，相关法律法规需要完善，相关体制机制需要健全。</p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; text-indent: 28px; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></p><p style=\"white-space: normal;\"><br/></p><p></p><section style=\"white-space: normal;\"><section><section><section><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\">监理企业与监理工程师的独立性问题</strong></p></section></section></section></section><p></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;监理企业与监理工程师到底属于哪一方，是代表业主还是独立的第三方？这个问题目前仍然没有得到解决。相关法律法规就这一问题存在相互矛盾的地方。如果要扭转目前监理行业的困境，<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">充分发挥是工程监理在工程建设方面的作用，建议从法律法规层面规定监理企业和监理工程师不代表业主，是独立的三方</span></strong>。</p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; widows: 1; text-align: center; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><img src=\"/ueditor/php/upload/image/20181212/1544602110.png\" title=\"哼哼办公\" alt=\"哼哼办公\" width=\"997\" height=\"734\" border=\"0\" vspace=\"0\" style=\"width: 997px; height: 734px;\"/></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; widows: 1; text-align: center; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(178, 178, 178); font-size: 14px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">建设市场新型三元模式</span></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; widows: 1; text-align: center; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;新型的监理、业主和承建商之间的关系如图所示。其要点一是<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">监理企业不是受业主的委托，而是规范业主的行为</span></strong>。多年的监理实践表明：业主的行为不规范甚至违纪违法，给工程建设带来负面影响，却长时间得不到有效的约束，是因为建设市场缺乏对业主的约束机制。只有引入监理工程师对业主的行为进行监督和规范，这种情况才能改变。要点二是<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">监理工程师不仅要监督承建商在工程建设方面的活动，还要服务于承建商</span></strong>。</p><p style=\"white-space: normal;\"><br/></p><p></p><section style=\"white-space: normal;\"><section><section><section><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\">安全监理的问题</strong></p></section></section></section></section><p></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;虽然《建设工程安全生产法规》和《2013版监理规范》已经把把安全监理的职责纳入了工程监理的任务之中。但是，仍然有不少业内人士对此提出质疑，认为我国的工程监理类似于国际上的工程咨询或项目管理，不应该承担安全生产责任。然而，面对施工现场不断频发的安全生产事故的严峻形势，从国情出发，监理企业与监理工程师应当承担安全生产的责任。<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">只是应从法律层面明确监理企业与监理工程师职责并界定其职责范围</span></strong>。虽然早在2006年原建设部出台的《安全生产监理责任的若干意见》就已经界定了监理企业与监理工程师安全生产责任，但是，由于是部门规章，其法律地位较低，各地在处理近几年发生的建筑工程安全生产事故的时候对监理工程师的处罚仍然偏重。比如，2010年在上海某高层住宅大火事件中，总监理工程师的量刑与项目经理一样重。这就混淆了生产者与监管者的责任。<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">建议《建筑法》与《安全生产法》明确监理企业与监理工程师的责任并科学合理地界定其责任范围</span></strong>。</p><p style=\"white-space: normal;\"><br/></p><p></p><section style=\"white-space: normal;\"><section><section><section><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\">工程监理的阶段范围问题</strong></p></section></section></section></section><p></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;工程监理是局限在施工阶段还是覆盖全过程？答案应该是前者，尽管监理理论囊括了决策、设计、招标、施工和保修等全过程。近些年来，为了振兴监理行业，原建设部出台了一系列关于监理企业向项目管理转换的部门规章。比如，2003年，原建设部印发了《关于培育发展工程总承包和工程项目管理企业的指导意见》；2008年，住建部印发了《关于大型工程监理单位创建工程项目管理企业的指导意见》的通知，都是鼓励监理企业升级转行，开展全过程的项目管理。一时间，很多监理企业为了拓宽业务范围，纷纷改变名称，把监理公司改成了项目管理公司。<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">然而，多年的实践表明，那些改换名称的监理企业仍然没能进行全过程监理工作。</span></strong>把工程监理局限在施工阶段是否是屈从于监理现状？显然不是，而是根据中国的国情，根据工程监理应起到主要作用所作出的合理选择。<strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\">工作监理的作用首先是促使承建商保证建设工程的质量并防止施工现场安全事故的发生，其次是为业主提高投资效益，而施工阶段是建设工程质量与安全事故的高发期。</span></strong>《建筑法》规定我国实施强制监理并把监理工作放在施工阶段就是考虑了这个因素。而全过程的项目管理不能替代施工阶段的工程监理，前者是市场行为和自愿的，后者是政府行为和强制的。建议监理教材应和法律法规、标准规范相互协调一致：工程监理的阶段范围是施工阶段。</p><p style=\"white-space: normal;\"><br/></p><p></p><section style=\"white-space: normal;\"><section><section><section><section></section></section><section><section><section><section><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">工程监理制度应从以下几个方面发生变化：</p><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;1、重新定义工程监理性质中“服务性”的概念，由传统观念中监理工程师只为业主服务改为既为业主服务，又为承建商服务。监理酬金由业主和承建商双方共同承担。这是监理工程师保持“独立性”与“公正性”或“公平性”的前提条件。</p><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;2、重新定位监理工程师的角色，监理工程师不代表业主，不受业主的委托，是独立的第三方。</p><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;3、在工程监理中引入监理工程师对业主的约束机制，即监理工程师不仅监督管理承建商的建设活动而且要规范业主的行为。</p><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;4、从法律层面明确和界定监理企业和监理工程师承担的安全生产责任与范围。</p><p style=\"padding: 0px; max-width: 100%; clear: both; min-height: 1em; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">&nbsp; &nbsp; &nbsp; &nbsp;5、法律法规、标准规范和监理理论应相互协调，明确工程监理的阶段是施工阶段。</p></section></section></section></section></section></section></section><p></p><p style=\"white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; line-height: 27.2px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><br/></p><p style=\"white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 17px; letter-spacing: 0.544px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px; margin-bottom: 10px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-size: 16px; letter-spacing: 0.544px; color: rgb(160, 160, 160); box-sizing: border-box !important; overflow-wrap: break-word !important;\">本文刊载于《建筑经济》201</span><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-size: 16px; letter-spacing: 0.544px; color: rgb(160, 160, 160); box-sizing: border-box !important; overflow-wrap: break-word !important;\">8年第四期（节选），原题：工程监理制度的问题分析与完善，作者：王家鼎（新疆建设职业技术学院）</span></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-size: 16px; letter-spacing: 0.544px; color: rgb(160, 160, 160); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><br/></span></p><p><br/></p>', '1', '1', '0', '1', null);
INSERT INTO `articles` VALUES ('51', '证书管理有难题？教你一招化解', '行业动态', null, '建筑资质证书和各类建筑人员证书影响着企业的经营发展，证书管理是企业管理中一项重要的管理工作，大量的人员资格证书使企业人工管理成本大大增加。高效的证书管理能够为企业带来更多的经济效益，建筑企业可以借助一些专业的管理工具科学管理证书，弥补管理漏洞，保障证书安全，避免企业低效率工作导致企业错失承接项目的机会。', 'http://www.hhbg.com/storage/MHVdmXzMnm1G581ZcoCmufyiqLU15LKSTYx6ojZf.jpeg', '建筑咨询企业管理系统,哼哼办公,OA管理系统', '5', '2018-12-12 08:40:26', '1', '<p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\">&nbsp;</span></strong></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\">建筑资质证书和各类建筑人员证书影响着企业的经营发展，证书管理是企业管理中一项重要的管理工作，大量的人员资格证书使企业人工管理成本大大增加。高效的证书管理能够为企业带来更多的经济效益，建筑企业可以借助一些专业的管理工具科学管理证书，弥补管理漏洞，保障证书安全，避免企业低效率工作导致企业错失承接项目的机会。</span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-weight: bold;\">一、</span><strong><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\">证书梳理分类</span></strong><strong><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\"></span></strong></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px;\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\">企业需要对当前所持有的证书进行总体盘点，并分类统计，根据企业现有证书，以企业发展目标为导向，分析出未来经营发展所需要的证书数量于现有证书存量的差距，提前做好相关证书来源规划。</span></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px;\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\">信息化管理系统，建筑人系统能够将企业证书进行系统性的统计和分类，方便全局查看证书。以人员证书为维度展示人员所对应的证书数量及证书类型，所有证书信息清晰可查，利于管理人员对于紧缺证书提前做好规划。</span></span></p><p style=\"white-space: normal; text-align: center; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\"><img src=\"http://www.58jzr.com/upload/default/20180621/d662b4ad1999db1ac0e90bcdea316a1c.jpg\" title=\"sfa.jpg\" alt=\"sfa.jpg\"/></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><strong><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px;\">二、证书精细化管理</span></span></strong></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">1.&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">企业需要安排专人随时对所有的证书进行细致的跟踪管理，防止证书到期之前无法及时办理延续。建筑人证书管理系统的优势在于首页展示证书预警，快到期证书预警提示，证书时间期限轻松掌握，便于管理者及时办理证书相关的延续手续。</span></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">2.&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">企业需要建立证书管理制度单独做表格记录证书</span><span style=\"font-family: 微软雅黑; line-height: 21px; letter-spacing: 0px; font-size: 14px;\">转让、借出、归还情况，证书规范借调审批流程，负责人签字确认，明确责任，防止证书在借调的过程中丢失追责困难。建筑人证书管理系统证书借调简单，所有流程线上解决，省去中间复杂的审批手续，借调记录和责任人一目了然。为企业省去大量的人工成本，提升办事效率。</span></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-indent: 28px; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">3.&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 14px;\">建筑人证书管理系统新增考试管理功能，便于证书管理员对即将过期的证书组织人员进行相应的继续教育管理，人员考试证书、状态、准考证领取时间、考试时间等信息一应俱全。</span></span></p><p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\">&nbsp;</span></p><p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\"><br/></span></p><p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\"><br/></span></p><p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\"><br/></span></p><p style=\"margin-bottom: 10px; white-space: normal; padding: 0px; background: rgb(255, 255, 255); line-height: 1.75em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; color: rgb(34, 34, 34); letter-spacing: 0px; font-size: 16px;\"><br/></span></p><p style=\"margin-bottom: 10px; white-space: normal; text-align: center; line-height: 1.75em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">建筑人管理系统助力建筑企业资质管理、证书管理、项目管理；到期预警更贴心！智能匹配更省心!</span></strong></span></p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('53', '北京：印发建设行业从业人员考试违纪违规行为处理办法（试行）的通知', '行业动态', null, '应试人员在考试过程中有下列严重违纪违规行为之一的，给予其当次全部科目考试成绩无效的处理，并将其违纪违规行为记入“北京市住房城乡建设行业从业人员考务与证书管理信息系统”，并在一年内不得参加行业各类从业人员考试：   （一）以旁窥，交头接耳，打手势，传递试卷、答题纸、答题卡、草稿纸等方式抄袭或协助他人抄袭试题答案或者与考试内容相关资料的；   （二）持伪造、变造证件和证明参加考试的；   （三）本人离开考场后，在考试结束前，传播考试试题及答案的；   （四）串通作弊或者参与有组织作弊的；', 'http://www.hhbg.com/storage/kq5HYwxcSL4wYHqMYXRgFxl0RUbTcW3JlDJgNKTW.png', '哼哼办公,猪太帅科技,建筑咨询企业管理系统,OA管理系统,企业OA系统', '1', '2018-12-12 09:09:52', '1', '<p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">京建法〔2018〕<span style=\"font-family: Calibri;\">21</span>号</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">各有关单位：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">为加强我市住房城乡建设行业从业人员考试管理工作，保证考试的公平、公正，规范对违纪违规行为的认定与处理，维护应试人员和考试工作人员合法权益，严肃工作纪律，我委制定了《北京市住房城乡建设行业从业人员考试违纪违规行为处理办法（试行）》，现予公布，并于2019年<span style=\"font-family: Calibri;\">1</span>月<span style=\"font-family: Calibri;\">1</span>日起施行。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 14px;\">北京市住房和城乡建设委员会</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 14px;\">2018年<span style=\"font-family: Calibri;\">11</span>月<span style=\"font-family: Calibri;\">8</span>日</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><hr style=\"white-space: normal;\"/><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\"></span><br/></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: center;\"><span style=\"font-family: 宋体; font-size: 14px;\">北京市住房城乡建设行业从业人员考试违纪违规行为处理办法（试行）</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第一章&nbsp;总　&nbsp;则</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第一条&nbsp;为加强我市住房城乡建设行业从业人员考试管理工作，保证考试的公平、公正，规范对违纪违规行为的认定与处理，维护应试人员和考试工作人员合法权益，严肃工作纪律，参照《中华人民共和国行政许可法》（中华人民共和国主席令第7号）《专业技术人员资格考试违纪违规行为处理规定》<span style=\"font-family: Calibri;\">(</span>人力资源和社会保障部令第<span style=\"font-family: Calibri;\">31</span>号<span style=\"font-family: Calibri;\">)</span>、《建筑施工企业主要负责人、项目负责人和专职安全生产管理人员安全生产管理规定》（住房和城乡建设部令第<span style=\"font-family: Calibri;\">17</span>号）的有关条款制定本办法。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第二条&nbsp;北京市住房城乡建设行业从业人员考试中违纪违规行为的认定和处理，适用本办法。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第三条&nbsp;北京市住房城乡建设行业从业人员考试，是指由北京市住房和城乡建设委员会核发证书的“建筑施工企业主要负责人，项目负责人和专职安全生产管理人员、住房和城乡建设领域专业人员、建设职业技能人员、建筑施工特种作业人员”的考试。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">本办法所称应试人员，是指参加北京市住房城乡建设行业从业人员考试的人员。</span></strong></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">本办法所称考试工作人员，是指参与考试管理和服务工作的人员，包括命题、审卷、监考、主考、巡考、考试系统操作、评卷等人员和考试主管部门及考试承办单位的有关工作人员。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第四条&nbsp;认定与处理违纪违规行为，应当事实清楚、证据确凿、程序规范、适用依据准确。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第五条&nbsp;北京市住房和城乡建设委员会负责本市住房和城乡建设行业从业人员考试工作的管理和监督。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">北京市建筑业执业资格注册中心受北京市住房和城乡建设委员会委托，负责从业人员考试具体实施工作。　　</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">第二章&nbsp;应试人员违纪违规行为处理</span></strong></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第六条&nbsp;应试人员隐瞒有关情况或者提供虚假材料申请考试的，不予考试，依法给予相应处理且一年内不得再次申请考试。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第七条&nbsp;应试人员在考试过程中有下列违纪违规行为之一的，给予其当次该科目考试成绩无效的处理：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）携带规定以外的物品进入考场、未按要求放在指定位置，经提醒仍不改正的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）经提醒仍不按规定书写、填涂本人身份和考试信息的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）在答题纸、答题卡规定以外位置标注本人信息或者其他特殊标记的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（四）未在规定座位参加考试，或者未经考试工作人员允许擅自离开座位或者考场的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（五）未用规定的纸、笔作答，或者试卷前后作答笔迹不一致的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（六）在考试开始信号发出前答题，或者在考试结束信号发出后继续答题的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（七）将试卷、答题卡、答题纸带出考场的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（八）故意损坏试卷、答题纸、答题卡、考试用具（设备设施、器械）电子化系统设施的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（九）未按规定使用考试系统，经提醒仍不改正的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十）其他应当给予当次该科目考试成绩无效处理的违纪违规行为。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第八条&nbsp;应试人员在考试过程中有下列严重违纪违规行为之一的，给予其当次全部科目考试成绩无效的处理，并将其违纪违规行为记入“北京市住房城乡建设行业从业人员考务与证书管理信息系统”，并在一年内不得参加行业各类从业人员考试：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）以旁窥，交头接耳，打手势，传递试卷、答题纸、答题卡、草稿纸等方式抄袭或协助他人抄袭试题答案或者与考试内容相关资料的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）持伪造、变造证件和证明参加考试的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）本人离开考场后，在考试结束前，传播考试试题及答案的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（四）串通作弊或者参与有组织作弊的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（五）代替他人或者让他人代替自己参加考试的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（六）一年内累计三次考试报名成功但不参加考试的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（七）利用通讯工具、电子设备或者其他技术手段接收、发送与考试相关信息的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（八）其他情节特别严重、影响恶劣的违纪违规行为。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;<img src=\"http://www.58jzr.com/upload/default/20181126/e0bda7f6a1229ba2205901c3dfd0be5d.png\" title=\"image.png\" alt=\"image.png\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第九条&nbsp;应试人员应当自觉维护考试工作场所秩序，服从考试工作人员管理，有下列行为之一的，终止其继续参加考试，并责令离开考场；情节严重的，按照本办法第七条、第八条的规定处理；违反《中华人民共和国治安管理处罚法》等法律法规的，交由公安机关依法处理；构成犯罪的，依法追究刑事责任：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）故意扰乱考点、考场等考试工作场所秩序的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）拒绝、妨碍考试工作人员履行管理职责的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）威胁、侮辱、诽谤、诬陷工作人员或者其他应试人员的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（四）其他扰乱考试管理秩序的行为。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十条&nbsp;应试人员通过欺骗、贿赂等其他不正当手段取得证书的，撤销其证书，且三年内不得再次申请考试；构成犯罪的，依法追究刑事责任。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十一条&nbsp;在阅卷评卷过程中发现应试人员有下列情形之一的，由北京市建筑业执业资格注册中心根据阅卷评卷专家组意见认定是否为作弊：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）同一科目试卷答案文字表述、主要错点高度一致，或者错同数量达到一定比例的，即为雷同试卷；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）在答题纸、答题卡规定以外位置标注本人信息或者其他特殊标记的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）试卷前后作答笔迹不一致的。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">第三章　考试工作人员违纪违规行为处理</span></strong></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十二条&nbsp;考试工作人员有下列情形之一的，停止其继续参加考试工作，并由有关部门依法给予处理：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）不严格掌握报名条件的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）擅自提前考试开始时间、推迟考试结束时间及缩短考试时间的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）擅自为应试人员调换考场或者座位的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（四）提示或者暗示应试人员答卷的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（五）未准确记录考场情况及违纪违规行为，并造成一定影响的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（六）未认真履行职责，造成考场秩序混乱或者所负责考场出现雷同试卷的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（七）未执行回避制度的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（八）其他一般违纪违规行为。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;<img id=\"loading_joxzd8yg\" src=\"http://www.58jzr.com/static/js/ueditor/themes/default/images/spacer.gif\" title=\"正在上传...\"/><img src=\"http://www.58jzr.com/upload/default/20181126/4b71d2c20466fc23dc7fb2aa8fa2175c.png\" title=\"image.png\" alt=\"image.png\"/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十三条&nbsp;考试工作人员有下列情形之一的，由有关部门将其调离考试工作岗位，不得再从事考试工作，并依法给予处理：</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（一）因命题、审卷发生错误，造成严重后果的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（二）以不正当手段协助他人取得考试资格或者取得相应证书的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（三）因失职造成应试人员未能如期参加考试，或者使考试工作遭受重大损失的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（四）擅自将试卷、试题信息、答题纸、答题卡、草稿纸等带出考场或者传给他人的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（五）故意损坏试卷、答题纸、答题卡、试题载体的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（六）窃取、擅自更改、编造或者虚报考试数据、信息的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（七）泄露考务实施工作中应当保密信息的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（八）在评阅卷工作中，擅自更改评分标准或者不按评分标准进行评卷的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（九）因评卷工作失职，造成卷面成绩错误，后果严重的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十）指使或者纵容他人作弊，或者参与考场内外串通作弊的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十一）监管不严，使考场出现大面积作弊现象的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十二）擅自拆启未开考试卷、试题载体、答题纸或者考试后已密封的试卷、试题载体、答题纸、答题卡的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十三）利用考试工作之便，以权谋私或者打击报复应试人员的；</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">（十四）其他严重违纪违规行为。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十四条&nbsp;考试工作人员违反有关规定，造成在保密期限内的考试试题、试卷及相关材料内容泄露或丢失的，由相关部门视情节轻重，分别给予责任人和有关负责人处理；构成犯罪的，依法追究刑事责任。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">第四章&nbsp;处理程序</span></strong></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十五条&nbsp;应试人员违纪违规行为被当场发现的，考试工作人员应当查实情况，收集、保存相应证据材料，如实记录违纪违规事实和现场处理情况，当场告知其记录内容并要求本人签字，对于拒绝签字或者恶意损坏证据材料的，由两名考试工作人员如实记录其拒签或恶意损坏证据材料的情况。违纪违规记录经考点负责人签字认定后，按要求报送。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">对应试人员违纪违规使用的物品，应当进行暂留保管，并填写清单。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十六条&nbsp;对应试人员违纪违规行为作出处理决定前，应当告知应试人员拟作出的处理决定及相关事实、理由和依据，并告知应试人员5个工作日内提出陈述和申辩。对应试人员提出的事实、理由和证据，应当进行复核并于<span style=\"font-family: Calibri;\">5</span>个工作日内回复。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">对应试人员违纪违规行为作出处理决定的，出具违纪违规行为处理决定书，依法送达被处理的应试人员。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十七条&nbsp;被处理的应试人员对处理决定不服的，可以依法申请行政复议或者提起行政诉讼。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十八条&nbsp;考试工作人员因违纪违规行为受到处理不服的，可以依法申请复核、申诉或者劳动仲裁。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">第五章&nbsp;附&nbsp;则</span></strong></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第十九条&nbsp;按照本办法一年内不得参加我市住房城乡建设行业各类从业人员考试的期限，应当按发生违规违纪行为之日起，按周年计算。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">第二十条&nbsp;本办法自2019年<span style=\"font-family: Calibri;\">1</span>月<span style=\"font-family: Calibri;\">1</span>日起施行。</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;</span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em;\"><span style=\"font-family: 宋体; font-size: 14px;\"><br/></span></p><p style=\"margin-top: 10px; white-space: normal; line-height: 1.75em; text-align: center;\"><strong><span style=\"font-family: 宋体; font-size: 14px;\">建筑人管理系统助力建筑企业资质管理、证书管理、项目管理；到期预警更贴心！智能匹配更省心！</span></strong></p><p><strong><span style=\"font-family: 宋体; font-size: 14px;\"><br/></span></strong></p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('54', '甘肃:出台拖欠农民工工资“黑名单”管理暂行办法实施细则', '行业动态', null, '为规范拖欠农民工工资“黑名单”管理工作，加强对拖欠工资违法失信用人单位的惩戒，维护广大农民工合法权益，甘肃省人社厅近日印发《甘肃省拖欠农民工工资“黑名单”管理暂行办法实施细则》(以下简称《实施细则》)。《实施细则》对用人单位列入拖欠工资“黑名单”的情形以及公布方式等方面予以明确。', 'http://www.hhbg.com/storage/3KTj1sfqzW2tsCOH1P2qxuDoYB9BTX55yJeykTY5.png', '建筑咨询企业管理系统, 哼哼办公, OA管理系统', '3', '2018-12-13 01:11:38', '1', '<p><br/></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">　　为规范拖欠农民工工资</span>“黑名单”管理工作，加强对拖欠工资违法失信用人单位的惩戒，维护广大农民工合法权益，甘肃省人社厅近日印发《甘肃省拖欠农民工工资“黑名单”管理暂行办法实施细则》<span style=\"font-family:Calibri\">(</span><span style=\"font-family:宋体\">以下简称《实施细则》</span><span style=\"font-family:Calibri\">)</span><span style=\"font-family:宋体\">。《实施细则》对用人单位列入拖欠工资“黑名单”的情形以及公布方式等方面予以明确。</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p style=\"text-align:center\"><img src=\"http://www.hhbg.com/ueditor/php/upload/image/20181213/1544663441.jpg\" title=\"1544663441.jpg\" alt=\"hn.jpg\"/></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\"></span><br/></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">　　《实施细则》明确，用人单位存在下列情形之一的，人力资源社会保障行政部门应当自查处违法行为并作出行政处理或处罚决定之日起</span>20<span style=\"font-family:宋体\">个工作日内，按照管辖权限将其列入拖欠工资“黑名单”。一是克扣、无故拖欠农民工工资报酬，数额达到认定拒不支付劳动报酬罪数额标准的，具体为：行为人拒不支付一名劳动者三个月以上的劳动报酬且数额在八千元</span><span style=\"font-family:Calibri\">(</span><span style=\"font-family:宋体\">含</span><span style=\"font-family:Calibri\">)</span><span style=\"font-family:宋体\">以上的，或拒不支付十名以上劳动者的劳动报酬且数额累计在六万元</span><span style=\"font-family:Calibri\">(</span><span style=\"font-family:宋体\">含</span><span style=\"font-family:Calibri\">)</span><span style=\"font-family:宋体\">以上的</span><span style=\"font-family:Calibri\">;</span><span style=\"font-family:宋体\">二是因拖欠农民工工资违法行为引发群体性事件、极端事件造成严重不良社会影响的，将劳务违法分包、转包给不具备用工主体资格的组织和个人造成拖欠农民工工资且符合前款规定情形的，应将违法分包、转包单位及不具备用工主体资格的组织和个人一并列入拖欠工资“黑名单”。</span></span></p><p style=\"line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p style=\"text-indent: 28px; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">《实施细则》指出，人力资源社会保障行政部门应当按照有关规定，将拖欠工资</span>“黑名单”信息纳入当地和全国信用信息共享平台，对被列入“黑名单”的用人单位或个人，由有关部门在各自职责范围内依法依规实施联合惩戒。“黑名单”惩戒对象将在政府资金支持、政府采购、招投标、生产许可、资质审核、融资贷款、市场准入、税收优惠、评优评先等方面受到限制。拖欠工资“黑名单”实行动态管理。列入“黑名单”的用人单位或个人信息，人力资源社会保障行政部门应当按照有关规定在部门门户网站、“信用甘肃”网站、“信用中国”网站、国家企业信用信息公示系统等平台上予以公示。</span></p><p style=\"text-indent: 28px; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p style=\"text-indent: 28px; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">来源：信用中国</span></span></p><p style=\"text-indent: 28px; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p style=\"text-indent: 28px; line-height: 1.75em; margin-top: 10px; margin-bottom: 10px;\"><br/></p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('55', '建筑项目管理存在的问题及应对措施', '行业动态', null, '建筑行业发展迅速，竞争激烈的市场对施工企业的管理水平提出了更高的要求。建筑企业项目管理在企业发展管理中占据较大的比重，项目管理工作包含内容较多，各个环节互相关联。建筑项目管理工作不到位，工程施工中存在的问题，直接或间接影响到工程的施工质量，也可能使建筑在后期投入使用的时候存在安全隐患。本文主要分析企业工程项目管理存在的问题及提出相应的对策，希望能为建筑企业的项目管理提供有价值的参考。', 'http://www.hhbg.com/storage/gI7J31QX0mwADaELawBlAzfQOIVlnZoFCZYJIFhw.jpeg', '哼哼办公', '0', '2018-12-13 01:34:41', '1', '<p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">建筑行业发展迅速，竞争激烈的市场对施工企业的管理水平提出了更高的要求。建筑企业项目管理在企业发展管理中占据较大的比重，项目管理工作包含内容较多，各个环节互相关联。建筑项目管理工作不到位，工程施工中存在的问题，直接或间接影响到工程的施工质量，也可能使建筑在后期投入使用的时候存在安全隐患。本文主要分析企业工程项目管理存在的问题及提出相应的对策，希望能为建筑企业的项目管理提供有价值的参考。</span></p><p style=\"white-space: normal; text-align: center; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\"><img src=\"https://www.58jzr.com/ueditor/php/upload/image/20181128/1543376250868694.jpg\" title=\"1543376250868694.jpg\" alt=\"4v.jpg\"/></span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑;\">一、建筑项目管理存在的问题</span></strong><strong><span style=\"font-family: 微软雅黑;\"></span></strong></span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">1.&nbsp;安全管理意识薄弱<br/>建筑企业重视生产，对安全施工的重要性认识不够全面，在安全方面的投入不够，在施工过程中安全知识培训和安全宣传力度不够。有企业为追求经济效益，可能会出现违规操作。企业内部管理人员结构组成相对缺乏合理性以及科学性，大部分管理人员专业素养以及能力水平参差不齐，安全意识薄弱，缺乏识别施工现场潜在的安全隐患的能力和发生安全事故后应急处理的能力。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">2.&nbsp;制度落实程度不够</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">大型建筑企业都有比较健全的工程施工生产秩序、管理条例等，但由于施工现场安全管理人员素质水平不高，没有对安全施工的重要性给与足够重视，无法将管理制度落实到实际的管理工作中，大部分管理制度只是流于形式，用于应付上级检查。</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">3.&nbsp;建筑施工项目管理缺乏整体协调性</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">建筑项目专业施工种类比较复杂，施工方面存在交叉分配。如果在建筑过程中的协调工作处理不得当，可能会影响工程施工进度和整体质量。工程施工现场人员工作责任和内容不明确，容易造成工作的遗漏或者工作内容重复，增加了建筑协调管理的复杂。管理人员对于建筑施工专业和新技术应用的知识学习更新不及时，管理水平跟不上变化，在专业施工方面的协调较困难，出现误工和返工的情况，进而影响整体工程的施工进度。</span></p><p style=\"white-space: normal; text-align: center; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\"><img src=\"https://www.58jzr.com/ueditor/php/upload/image/20181128/1543376280191277.jpg\" title=\"1543376280191277.jpg\" alt=\"152.jpg\"/></span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">二、应对措施</span></strong></span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">1.加大安全管理工作力度，建立施工管理体系</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">加强施工全过程的安全管理是避免建筑工程施工安全事故发生的重要举措。建筑企业之间相互合作，组织安全管理人员相互学习，双方相互借鉴优秀的管理制度和经验，将先进的管理理念引入到企业管理体系中，建立适合企业自身的管理体系。制定相应的安全管理制度、项目管理考核与奖惩制度，调整优化管理人员结构，提升管理人员整体管理水平和专业能力。确保将管理人员责任落到实处，明确责任人，出现质量问题或者事故对管理人员直接问责。定期对施工人员进行安全培训，加强全体员工的安全意识，实时监测施工全过程，及时发现和解决施工中的隐患问题。</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">2.强化组织协调工作</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; font-size: 16px;\">建立项目施工组织调度机构，优选具备专业能力和管理能力的项目管理总负责人，配备专业的调度员，形成动态控制体系。由负责人统一指挥，调度人员与专业施工人员加强沟通交流，不同环节分组配合工作；及时更新调度员和施工人员的专业技术，保障在施工过程中的协调有序到位，工作命令清晰防止出现重复和矛盾的情况，使协调工作更加科学合理。</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 16px;\">3.创新升级</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 16px;\">建筑企业创新管理方式，在管理方面投入更多成本，引进或者培养专业的管理人才，采用科学有效的管理方式，提升企业管理水平。在技术方面，鼓励技术创新，采用新材料、先进的装备、提高机械化施工，建立自动化集成系统，使管理更加高效和合理。</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 16px;\">三、小结</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 微软雅黑; line-height: 21px; font-size: 16px;\">建筑项目管理是整个工程质量得以保证的重要基础，加强工程项目管理和技术创新，有利于建筑企业的持久发展。</span></p><p style=\"white-space: normal; line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\">&nbsp;</span></p><p style=\"line-height: 2em; margin-top: 10px;\"><span style=\"font-family: 宋体; font-size: 16px;\"><br/></span></p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('56', '多地推出城市“信用分”无信用者将寸步难行', '行业动态', null, null, 'http://www.hhbg.com/storage/n3bLL66wENdmOdXglDO5Vi7TzfvHJu6eLHpg8TJD.jpeg', null, '0', '2018-12-13 01:35:56', '1', '<p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">个人诚信是社会诚信的基石。所以，“个人诚信分”工程有利于助推信用社会的构建。目前，已有多地推出城市“信用分”工程，助推信用社会构建。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); text-align: center; margin-top: 10px; text-size-adjust: none !important;\"><img src=\"/ueditor/php/upload/image/20181213/1544664922.jpg\" alt=\"\"/></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　<strong style=\"margin: 0px; padding: 0px;\">2020年底覆盖全部常住人口 北京重磅推出《行动计划》</strong></span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　日前，《北京市进一步优化营商环境行动计划(2018年—2020年)》（以下简称“《行动计划》”）正式发布。《行动计划》明确，2020年底前完成北京市社会信用条例立法工作，并建成覆盖全部常住人口的北京“个人诚信分”工程。这意味着，北京将真正开启对个人信用的评价，个人诚信分将关系终身。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　2020年底前，北京市将建成“个人诚信分”制度，覆盖北京全部常住人口，守信者将获得“绿色通道”等便利，失信者将“一处失信、处处受限、寸步难行”，付出沉重代价。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　《行动计划》提出，2020年底前将完成北京市社会信用条例立法，建设全市统一的公共信用信息服务平台；建立健全数据清单、行为清单、措施清单等全市统一的信用联合奖惩“三清单”制度，2020年底前建成覆盖全部常住人口的北京“个人诚信分”工程，推动信用信息在市场准入、公共服务、旅游出行、创业求职等领域的广泛应用。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　守信者和失信者将分别得到激励和惩戒。《行动计划》提出，将为守信者提供“容缺受理”“绿色通道”便利措施以及“信易+”示范项目激励措施；违法违规失信者却要付出沉重代价，通过完善信用黑名单制度，定期公开公示企业和个人失信记录，将形成“一处失信、处处受限、寸步难行”的失信惩戒格局。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　<strong style=\"margin: 0px; padding: 0px;\">杭州推出“钱江分” 市民可凭分数享受系列信用服务</strong></span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　北京市关义信用分的建设不是个例，11月16日，杭州推出城市个人信用分“钱江分”。凭分数即可享受校园健身、公交扫码先享后付、智慧医疗医信付等系列信用服务……</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　据了解，“钱江分”依托杭州市公共信用信息平台及杭州市民卡运营十余年积累的用户数据，相较于芝麻分等其他信用分，分数设计侧重于公共服务和公益普惠，目前已在杭州办事服务App、杭州市民卡App和信用杭州AApp上线，市民登录授权后即可查看本人的“钱江分”。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　目前，“钱江分”已实现校园健身、公交扫码先享后付、智慧医疗“医信付”等应用场景，计划2018年年底前，上线公租房免押金、免押办理图书借阅证、地铁扫码先享后付等应用场景。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　据介绍，明年“钱江分”还将陆续应用到健身场馆预约、停车预约、小客车摇号、博物馆和景区活动线上报名、二手房交易等与市民密切相关的城市生活中。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　<strong style=\"margin: 0px; padding: 0px;\">多地推出城市“信用分”无信用者将寸步难行</strong></span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　今年1月，国家发展改革委、央行公布了首批杭州市、南京市、厦门市、成都市、苏州市、宿迁市、惠州市、温州市、威海市、潍坊市、义乌市、荣成市等12个社会信用体系建设示范城市名单。截至目前，首批12个信用城市已发布城市“信用分”的有杭州市“钱江分”、厦门市“白鹭分”、苏州市“桂花分”、宿迁市“西楚分、威海市“海贝分”。</span></p><p style=\"margin-bottom: 0px; white-space: normal; padding: 0px; font-family: &quot;Microsoft YaHei&quot;; -webkit-tap-highlight-color: rgba(255, 255, 255, 0); list-style: none inside; border: 0px; user-select: text; font-size: 14px; line-height: 2; color: rgb(102, 102, 102); letter-spacing: 1px; background-color: rgb(246, 246, 246); margin-top: 10px; text-size-adjust: none !important;\"><span style=\"font-size: 16px;\">　　近年来，在诚信社会建设方面，尤其是在治理失信行为上面，国家下了很多功夫，如建立黑名单制度。随着联合惩戒的范围越来越广，越来越多的行业都建立起黑名单，一些不良行为，不诚信事情，都将记录在案，各部门联合惩戒。唯有让失信者心有畏惧，守信者得到实惠，才能让诚信分更显金贵。当然，这就有赖于两点，一是诚信分评价的公平公正性，即确保个人信用评价，既让当事人心服口服，也能够经得起群众的考验；二是部门联合奖惩机制须完善，这也就需要各部门之间完善数据共享机制，从而让个人信用直接影响到其自身的办事成效。</span></p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('57', '二级建造师挂靠协议怎么签才保险？', '行业动态', null, '当我们将二级建造师证书用于挂靠时，必定会经过二级建造师挂靠协议签订这一环节，那么当我们与企业签订二级建造师挂靠协议时需要注意哪些方面呢?下面就由小编为您解析：', 'http://www.hhbg.com/storage/Z0sdkx3JeQFayeAazrfD0MN14HHpu0po55AairRZ.png', null, '0', '2018-12-13 01:37:08', '1', '<blockquote style=\"white-space: normal; border-width: 0px 0px 0px 4px; border-left-style: solid; border-left-color: rgb(204, 204, 204); margin: 0.63em 0px 0px; padding: 0px 0px 0px 16px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\"><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; border: 0px; padding: 0px;\">当我们将二级建造师证书用于挂靠时，必定会经过二级建造师挂靠协议签订这一环节，那么当我们与企业签订二级建造师挂靠协议时需要注意哪些方面呢?下面就由小编为您解析：</p></blockquote><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"font-weight: 700; border: 0px; margin: 0px; padding: 0px;\">　挂靠协议签订要考虑以下方面</span></p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">1、挂资质和印章问题执业印章在注册通过后才能拿到，也不一定非要本人才能拿，公司也可能拿到。建议必须本人亲自拿到手才好，一定要标明是给公司升资质用，不能作为投标项目经理证使用，挂证不挂章，这些都要在合同里说明。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">2、证书丢失怎么办?对于证书丢失的问题要协商好，证书丢失要求企业协义补办，实在是补办不了的要注明赔偿责任，注明赔偿金额，以增加企业责任，好保管好证书。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">3、保险怎么办?有的地方要求交保险，有的地方不要求，如果工资里面含保险，那就是另外的一回事了。如果需要转保险的，一定要和挂证公司协商好。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">4、企业不支付费用怎么办?其实这是一个很难的问题，建议签定一份正式的合同，这样可以绕过挂靠协议，挂靠前应核实企业的资质，一般来讲大的企业会相对有保障一些。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">5、继续教育怎么办?这是很重要的问题，继续教育的费用还是很高的，所以协议中需要注明继续教育的费用由企业承担。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"https://www.58jzr.com/ueditor/php/upload/image/20181121/1542764040133381.jpeg\"/></p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">6、转注册问题很多人都遇到过转注册企业不配合的问题，所以协议中应注明挂靠合同期满企业应该配合转注册，企业应提交的资料，合同违约处理，协商调解等。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">7、挂靠费用问题协议中的挂靠费用，二级建造师和企业要仔细协商，并且要确定好支付方式。协议中要注明薪水是按月发放还是按年发放，是在签定协议时发放还是在证书注册成功公示后发放。这一点很重要，有的建造师合同签的很模糊，最后出了问题。另需明确什么时候付薪水，怎么付，付多少，是预付还是怎么付等。二级建造师证书挂靠协议签几年比较好?这个没有硬性规定，一般的1—3年都可以的，有的人可能觉得行情每年都在涨或者觉得转注册费用高，希望一年一转;有的可能觉得转来转去的麻烦，不如直接签个两三年的，这个一般看个人的意愿和企业的要求。如果说变数比较大的话，建议签订一年比较好。</p><p style=\"margin-top: 0.63em; margin-bottom: 1.8em; white-space: normal; border: 0px; padding: 0px; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; line-height: 30.4px; widows: 1; background-color: rgb(255, 255, 255);\">从建造师的角度而言，在遇到挂靠市场行情比较好的时候或者急需要一比资金的时候，当然是想签定的时间越长越好，从单位的角度，当挂靠价格比较低迷的时候或项目工期要求，自然想签订的时间要长一些，但由于政策影响、企业资金和继续教育等问题，一般企业签订的挂靠协议最多不会超过3年。二级建造师证书挂靠协议有法律效力吗?“建造师挂靠”本身的行为其实是不被法律认同的，所以挂靠协议是没有法律效力的，各地法院在审理涉及挂靠纠纷时，对于挂靠人与被挂靠企业签订的《合作协议》、《分包协议》或《内部承包协议》一般都认定为无效。虽说挂靠本身不合法，但是协议还是需要签订，在表面上对双方有一定约束作用，若没有与被挂靠的公司签订协议，则自己还有可能需要面对违约的风险。</p><p><br/></p>', '1', '0', '0', '1', null);
INSERT INTO `articles` VALUES ('58', '设计施工一体化资质企业资质证书变更需要这些材料', '行业动态', null, '申请条件    勘察设计企业需变更资质证书内容的应在工商行政管理部门办理相关变更手续后30日内到当地建设行政主管部门办理资质证书内容变更。   申请材料    变更事项申报材料（以下材料提交复印件，校验原件）', 'http://www.hhbg.com/storage/VuGZZdkLxKsP2P7qxu2NM14Fg5kn9E8DEhPHfYJB.jpeg', '哼哼办公', '3', '2018-12-13 01:42:45', '1', '<p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体; font-size: 14px;\">&nbsp;哼哼办公小编今天给大家介绍的是设计施工一体化资质企业资质证书变更需要的申请材料及申请条件<br/></span></p><p style=\"white-space: normal; line-height: 2em;\"><strong><span style=\"font-family: 宋体;\">申请条件</span></strong></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\"> 勘察设计企业需变更资质证书内容的应在工商行政管理部门办理相关变更手续后<span style=\"font-family: Calibri;\">30</span>日内到当地建设行政主管部门办理资质证书内容变更。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><strong><span style=\"font-family: 宋体;\">申请材料</span></strong></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\"> 变更事项申报材料（以下材料提交复印件，校验原件）</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\"> <strong> （一）材料清单、排列顺序：</strong></span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">1</span>、企业出具由法定代表人、执行合伙企业事务的合伙人签署的资质证书变更申请；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">2</span>、《建设工程企业资质变更审核表》（在“四川省勘察设计企业管理系统”、“四川省设计施工一体化企业管理信息系统”中填报、打印）；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">3</span>、资质证书正、副本原件（除注册名称变更外，只需提供副本原件）；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">4</span>、变更前、后的企业法人、合伙企业营业执照副本复印件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">5</span>、变更前、后的企业章程复印件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">6</span>、股东会相关决议；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">7</span>、《工程勘察、工程设计企业基本信息表》；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">8</span>、与资质变更事项有关的证明材料：</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">1</span>）企业名称变更的，需通过企业勘察设计信息管理系统，以变更后的名称申报填写资质申请表并提交；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">2</span>）法定代表人或执行合伙企业事务的合伙人、企业经理或负责人、技术负责人变更的，提供他们的身份证、职称证、毕业证；企业经理或负责人、技术负责人提供劳动合同、社保证明材料，技术负责人还提供“专业技术人员基本情况及业绩表”</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">3</span>）地址变更的提交新的办公场地的自有产权证明或租赁（借）合同和所租（借）场地的产权证明。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">9</span>、上级主管部门关于企业改制的文件。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  <span style=\"font-family: Calibri;\">10</span>、开户银行出具的开户证明。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"text-align:center\"><img src=\"http://www.hhbg.com/ueditor/php/upload/image/20181213/1544665297.jpg\" title=\"1544665297.jpg\" alt=\"1544665297.jpg\" width=\"900\" height=\"577\" border=\"0\" vspace=\"0\" style=\"width: 900px; height: 577px;\"/></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\"></span><br/></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\"> <strong> （二）如下变更事项需提交以下材料：</strong></span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  企业名称（省住房和城乡建设厅审批），注册资本金变更（当地建设行政主管部门）：提交第（一）项的：<span style=\"font-family: Calibri;\">1</span>、<span style=\"font-family: Calibri;\">2</span>、<span style=\"font-family: Calibri;\">3</span>、<span style=\"font-family: Calibri;\">4</span>、<span style=\"font-family: Calibri;\">5</span>、<span style=\"font-family: Calibri;\">6</span>、<span style=\"font-family: Calibri;\">7</span>、<span style=\"font-family: Calibri;\">8</span>（<span style=\"font-family: Calibri;\">1</span>）等共<span style=\"font-family: Calibri;\">8</span>条。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  法定代表人（合伙企业事务的合伙人）、单位负责人（当地建设行政主管部门），技术负责人（省住房和城乡建设厅审批）变更：提交第（一）项的：<span style=\"font-family: Calibri;\">1</span>、<span style=\"font-family: Calibri;\">2</span>、<span style=\"font-family: Calibri;\">3</span>、<span style=\"font-family: Calibri;\">4</span>、<span style=\"font-family: Calibri;\">5</span>、<span style=\"font-family: Calibri;\">6</span>、<span style=\"font-family: Calibri;\">7</span>、<span style=\"font-family: Calibri;\">8</span>（<span style=\"font-family: Calibri;\">2</span>）等共<span style=\"font-family: Calibri;\">8</span>条。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  详细地址（当地建设行政主管部门）：提交第（一）项的：<span style=\"font-family: Calibri;\">1</span>、<span style=\"font-family: Calibri;\">2</span>、<span style=\"font-family: Calibri;\">3</span>、<span style=\"font-family: Calibri;\">4</span>、<span style=\"font-family: Calibri;\">6</span>、<span style=\"font-family: Calibri;\">8</span>（<span style=\"font-family: Calibri;\">3</span>）等共<span style=\"font-family: Calibri;\">6</span>条。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  经济性质（当地建设行政主管部门）：提交第（一）项的：<span style=\"font-family: Calibri;\">1</span>、<span style=\"font-family: Calibri;\">2</span>、<span style=\"font-family: Calibri;\">3</span>、<span style=\"font-family: Calibri;\">4</span>、<span style=\"font-family: Calibri;\">5</span>、<span style=\"font-family: Calibri;\">6</span>、<span style=\"font-family: Calibri;\">9</span>等共<span style=\"font-family: Calibri;\">7</span>条。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  营业执照注册号（当地建设行政主管部门）：提交第（一）项的：<span style=\"font-family: Calibri;\">1</span>、<span style=\"font-family: Calibri;\">2</span>、<span style=\"font-family: Calibri;\">3</span>、<span style=\"font-family: Calibri;\">4</span>、<span style=\"font-family: Calibri;\">9</span>等共<span style=\"font-family: Calibri;\">5</span>条。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  工商注册地跨市、州（扩权县）变化处理（省住房和城乡建设厅审批）：</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">1</span>）企业原工商注册地市（扩权县）建设行政主管部门（跨省的需提供省级主管部门）同意资质变更的书面意见原件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">2</span>）新工商注册地市（扩权县）建设行政主管部门同意资质变更的书面意见原件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">3</span>）变更前原工商营业执照注销证明复印件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">4</span>）企业股东大会或董事会关于工商注册地变更的决议或文件复印件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">5</span>）企业章程及章程修正案复印件；</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">  （<span style=\"font-family: Calibri;\">6</span>）《建设工程勘察设计资质管理规定实施意见》（建市<span style=\"font-family: Calibri;\">(2007)202</span>号）第（十一）条所列的全部材料。</span></p><p style=\"white-space: normal; line-height: 2em;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"white-space: normal; line-height: 2em;\"> </p><p><br/></p>', '1', '0', '0', '1', null);

-- ----------------------------
-- Table structure for authorities
-- ----------------------------
DROP TABLE IF EXISTS `authorities`;
CREATE TABLE `authorities` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `menu_id` int(11) NOT NULL COMMENT '所属菜单分类',
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '权限名称',
  `description` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '权限描述',
  `keyword` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '权限标识',
  `type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '类型：{0:接口,1:前端路由}',
  `default` tinyint(4) NOT NULL DEFAULT '0' COMMENT '类型：{0:不选中,1:默认选中,2:强制选中}',
  `extented` text COLLATE utf8mb4_unicode_ci COMMENT '权限扩展配置',
  `sort` smallint(6) NOT NULL DEFAULT '1000' COMMENT '排序',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

